import streamlit as st 
import pandas as pd 
import numpy as np

st.title("Welcome to BMW")
st.write(":streamlit: Hello")
st.text("lets Burnn it")

name=st.text_input("Enter name:")
if st.button("greet"):
    st.success(f"hello,{name}")

df=pd.DataFrame(np.random.randn(10,2),columns=['A','B'])
st.line_chart(df)
st.bar_chart(df)    

st.sidebar.title("Navigation")
st.image('data:image/jpeg;base64,',caption="this is a sample image")
st.video('https://youtu.be/Qw-RBUvEcao?si=V2c7R8HQrnG-idlv')

upload_csv=st.file_uploader('upload a csv file',type="csv")

if upload_csv:
    df=pd.read_csv(upload_csv)
    st.dataframe(df)

st.header("This is BMW")
st.subheader("This is M4 Competition")

st.markdown("**Bold**, *Italic*,'code[BMW india](https://www.bmw.in/en/index.html)")
st.markdown("**Bold**, *Italic*,'code[Link](https://streamlit.io)")
st.code("for i in range(5):print(i)",language="python")

st.text_input("what is your name ?")
st.text_area("write something")
st.number_input("pick a number",min_value=0, max_value=100)
st.slider("choose a range",0,100)
st.selectbox("select a model",["M2 comp","M3 comp","M4 comp","M5 comp"])
st.multiselect("choose colors",["matt black","neon","nardo","red","blue"])
st.radio("pick a class",["coupe","sedan"])
st.checkbox("i accept terms & conditions")

import matplotlib.pyplot as plt
fig, ax=plt.subplots()
